import { MachineConfig, AnyStateNodeDefinition } from "xstate";
import { IContext, IMachineEvents } from "./types";

const config: MachineConfig<IContext, AnyStateNodeDefinition, IMachineEvents> =
    {
        initial: "loading",
        states: {
            on: {
                SEARCH_INPUT: {
                    actions: [
                        (_, event) =>
                            console.log("SEARCH INPUT RECIEVED:", event),
                    ],
                    target: "search",
                },
            },
            loading: {},
            search: {
                entry: [
                    // (_, event) => console.log("INSIDE SEARCH STATE:", event),
                ],
            },
        },
    };

export default config;
