import React, { useState, useEffect } from "react";

interface IDebounceParams {
    input: string;
    delay: number;
    dependency: string;
}
export const useDebounce = ({ input, delay, dependency }: IDebounceParams) => {
    const [state, setState] = useState();

    useEffect(() => {
        // do something
        const timeout = setTimeout(() => {}, delay);
        return () => {
            clearTimeout(timeout);
        };
    }, [dependency]);
};
