import React, { useState, useEffect } from "react";

interface IDebounceParams {
    input: string;
    delay: number;
    dependency: string;
    actions: () => void;
}
export const useDebounce = ({
    input,
    delay,
    dependency,
    actions,
}: IDebounceParams) => {
    const [state, setState] = useState();

    useEffect(() => {
        // do something
        const timeout = setTimeout(() => {
            actions;
        }, delay);
        return () => {
            clearTimeout(timeout);
        };
    }, [dependency]);

    console.log("DEBOUNCE INPUT:", input, dependency);
};
